import React,{ useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {  faTrash } from '@fortawesome/free-solid-svg-icons'
import './App.css';
import { 
  Container,
  Row,
  Button,
  Nav,
  NavItem,
  NavLink,
  ListGroup,
  ListGroupItem,
  UncontrolledPopover,
  PopoverHeader,
  PopoverBody
 } from 'reactstrap';
import Movie from './components/Movie'

function App() {




 const [moviesList, setMovies] = useState([]);

 useEffect(() => {
   async function loadData() {
    var data = await fetch('http://localhost:3001/new-movies');
    var json = await data.json();
    setMovies(json.movies);
}
loadData()
}, []);
console.log(moviesList)
var movieList = moviesList.map(movie => {
  var inList = false
      if(wishList.find(e => e.name === movies.name)){
        inList = true
      }
    return(<Movie key={i} inList={inList} movieName={movie.title} movieDesc={movie.overview} movieImg={movie.poster_path} globalRating={movie.vote_average} globalCountRating={movie.vote_count} handleClickAddMovie={handleClickAddMovie}/>)
  })

// var moviesData = [
//   {name:"Star Wars : L'ascension de Skywalker", desc:"La conclusion de la saga Skywalker. De nouvelles légendes vont naître dans cette ...", img:"/starwars.jpg", note:6.7, vote:5},
//   {name:"Maléfique : Le pouvoir du mal", desc: "Plusieurs années après avoir découvert pourquoi la plus célèbre méchante Disney avait un cœur ...", img:"/maleficent.jpg", note:8.2, vote:3},
//   {name:"Jumanji: The Next Level", desc: "L’équipe est de retour, mais le jeu a changé. Alors qu’ils retournent dans Jumanji pour secourir ...", img:"/jumanji.jpg", note:4, vote:5},
//   {name:"Once Upon a Time... in Hollywood", desc: "En 1969, Rick Dalton – star déclinante d'une série télévisée de western – et Cliff Booth ...", img:"/once_upon.jpg", note:6, vote:7},
//   {name:"La Reine des neiges 2", desc: "Elsa, Anna, Kristoff, Olaf et Sven voyagent bien au-delà des portes d’Arendelle à la recherche de réponses ...", img:"/frozen.jpg", note:4.6, vote:3},
//   {name:"Terminator: Dark Fate", desc: "De nos jours à Mexico. Dani Ramos, 21 ans, travaille sur une chaîne de montage dans une usine automobile...", img:"/terminator.jpg", note:6.1, vote:1},
// ]




  const [compteurMovie, setCompteur] = useState(0);
  const [wishList, setWish] = useState([]);
  var handleClickAddMovie = (status,name,img) => {
    if(status === false){
      setCompteur(compteurMovie+1)
      setWish( [...wishList, {name, img}] );

    }if(status === true ){  
      if(compteurMovie > 0){
        setCompteur(compteurMovie-1) 
        setWish(wishList.filter(movie => movie.name !== name))
      }

    }
  }
  var removeWishlist = (name)=> {
    setWish(wishList.filter(movie => movie.name !== name))

    if(compteurMovie > 0){
      setCompteur(compteurMovie-1) 
    }
  }

  // var movieList = moviesData.map((movie,i) => {

  //     var inList = false
  //     if(wishList.find(e => e.name === movie.name)){
  //       inList = true
  //     }
  //   return(<Movie key={i} inList={inList} movieName={movie.name} movieDesc={movie.desc} movieImg={movie.img} globalRating={movie.note} globalCountRating={movie.vote} handleClickAddMovie={handleClickAddMovie}/>)
  // })

  var movieWishlist = wishList.map(movie => {
     return(
     <ListGroup>
        <ListGroupItem>
          <div className="wishlist">
            <img src={movie.img} alt="movieImg" width="100"/>
            <div className="wishlistItem">
              {movie.name}
            </div>
            <FontAwesomeIcon style={{cursor:"pointer"}} onClick={ ()=>removeWishlist(movie.name) } className="wishlistItem" icon={faTrash} />
          </div>
        </ListGroupItem>
     </ListGroup>)})

  return (
    <div style={{backgroundColor:"#232528"}}>
      <Container>
        <Nav>
          <span className="navbar-brand">
            <img src="./logo.png" width="30" height="30" className="d-inline-block align-top" alt="logo" />
          </span>
          <NavItem>
            <NavLink style={{color:'white'}}>Last Releases</NavLink>
          </NavItem>
          <NavItem>
            <div>
              <Button id="UncontrolledPopover" type="button">
                {compteurMovie} Film(s)
              </Button>
              <UncontrolledPopover placement="bottom" target="UncontrolledPopover">
                <PopoverHeader>
                  Wishlist
                </PopoverHeader>
                <PopoverBody>
                {movieWishlist}
                </PopoverBody>
              </UncontrolledPopover>
            </div>
          </NavItem>
        </Nav>
        <Row>
          {movieList}
        </Row>
      </Container>
    </div>
  );
}

export default App;
