import React, { useState } from 'react';
import '../App.css';
import { 
  Button,
  Col,
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardText,
  Badge,
  ButtonGroup,
 } from 'reactstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart, faStar, faVideo} from '@fortawesome/free-solid-svg-icons'

function Movie(props) {

  var tabGlobalRating = []
  for(var i=0;i<10;i++){
      var color = {}
      if(i<props.globalRating){
          color = {color: '#f1c40f'}
      }
      tabGlobalRating.push(<FontAwesomeIcon style={color} icon={faStar} /> )
  }
  const [likeclick, setLike] = useState(true)
  const [viewclick, setViewclick] = useState(true)
  const [countWatchMovie, setCount] = useState(0)
  const [myRatingMovie, setRating] = useState(0)

  var handleClick = ()=> {
    if(likeclick === true){
      setLike(false)
    }
    if(likeclick === false){  
      setLike(true) 
    }
 }
 var handleClick2 = ()=> {
  if(viewclick === true){
    setCount(countWatchMovie+1)
    setViewclick(false)
  }
  if(viewclick === false){  
    setCount(countWatchMovie-1)
    setViewclick(true) 
  }
}
var handleClick3 = ()=> {
  if(myRatingMovie > -1){
    setRating(myRatingMovie-1)
  }
  console.log(myRatingMovie)
}
var handleClick4 = ()=> {
  if(myRatingMovie < 11){
    setRating(myRatingMovie+1)
  }

    console.log(myRatingMovie)
  }


 var colorLike= {}
  var colorView= {}
 if(likeclick === true){
  colorLike={cursor: "pointer"}
}else{
  colorLike={color: '#e74c3c', cursor: "pointer"}}
if(viewclick === true){
  colorView={cursor: "pointer"}
}else{
  colorView={color: '#e74c3c', cursor: "pointer"}}

  if(myRatingMovie === 0){}
  else if(myRatingMovie === 1 || myRatingMovie > 1){
    var rateColor1 = {color: '#f1c40f'}
  }else if(myRatingMovie === 2 || myRatingMovie > 2){
    var rateColor2 = {color: '#f1c40f'}
  }else if(myRatingMovie === 3  || myRatingMovie > 3){
    var rateColor3 = {color: '#f1c40f'}
  }else if(myRatingMovie === 4 || myRatingMovie > 4){
    var rateColor4 = {color: '#f1c40f'}
  }else if(myRatingMovie === 5  || myRatingMovie > 5){
    var rateColor5 = {color: '#f1c40f'}
  }else if(myRatingMovie === 6 || myRatingMovie > 6){
    var rateColor6 = {color: '#f1c40f'}
  }else if(myRatingMovie === 7 || myRatingMovie > 7){
    var rateColor7 = {color: '#f1c40f'}
  }else if(myRatingMovie === 8 || myRatingMovie > 8){
    var rateColor8 = {color: '#f1c40f'}
  }else if(myRatingMovie === 9 || myRatingMovie > 9){
    var rateColor9 = {color: '#f1c40f'}
  }else if(myRatingMovie === 10 || myRatingMovie > 10){
    var rateColor10 = {color: '#f1c40f'}
  }

  return (
    <Col xs="12" lg="6" xl="4">
    <Card style={{marginBottom:30}}>
    <CardImg top src={props.movieImg} alt={props.movieName} />
    <CardBody>
        <p>Like<FontAwesomeIcon style={colorLike} onClick={ ()=>handleClick() } icon={faHeart} /></p>
        <p>Nombre de vues  <FontAwesomeIcon icon={faVideo} style={colorView} onClick={ ()=>handleClick2() }/> <Badge color="secondary">{countWatchMovie}</Badge></p>
        <p>Mon avis 
        <FontAwesomeIcon style={rateColor1} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor2} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor3} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor4} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor5} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor6} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor7} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor8} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor9} icon={faStar} /> 
        <FontAwesomeIcon style={rateColor10} icon={faStar} /> 

        <ButtonGroup size="sm">
            <Button onClick={ ()=>handleClick3() }color="secondary">-</Button>
            <Button onClick={ ()=>handleClick4() }color="secondary">+</Button>
        </ButtonGroup>
        </p>
        <p>Moyenne
        {tabGlobalRating}
        ({props.globalCountRating})
        </p>
  <CardTitle>{props.movieName}</CardTitle>
        <CardText>{props.movieDesc}</CardText>
    </CardBody>
    </Card>
    </Col>


  );
}

export default Movie;
